styles:
.hero-section {
    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
    color: white;
    text-align: center;
    padding: 80px 20px;
    margin-bottom: 40px;
}

.hero-section h1 {
    font-size: 3rem;
    margin-bottom: 20px;
    font-weight: 700;
}

.hero-subtitle {
    font-size: 1.2rem;
    opacity: 0.9;
    max-width: 600px;
    margin: 0 auto;
}

.container {
    max-width: 1200px;
    margin: 0 auto;
    padding: 0 20px;
}

.intro-section {
    margin-bottom: 50px;
    text-align: center;
}

.intro-text {
    font-size: 1.1rem;
    color: #666;
    max-width: 800px;
    margin: 0 auto;
    line-height: 1.6;
}

.formations-section, .financing-section {
    margin-bottom: 60px;
}

.formations-section h2, .financing-section h2 {
    color: #333;
    font-size: 2rem;
    margin-bottom: 30px;
    border-bottom: 3px solid #667eea;
    padding-bottom: 10px;
}

.formation-card, .financing-card {
    background: white;
    border-radius: 15px;
    padding: 30px;
    margin-bottom: 25px;
    box-shadow: 0 5px 20px rgba(0,0,0,0.1);
    border-left: 5px solid #667eea;
}

.formation-card h3, .financing-card h3 {
    color: #333;
    font-size: 1.3rem;
    margin-bottom: 15px;
}

.formation-card p, .financing-card p {
    color: #666;
    line-height: 1.6;
    margin-bottom: 15px;
}

.formation-card ul {
    color: #666;
    line-height: 1.6;
    margin-left: 20px;
}

.highlight {
    background: #f8f9ff;
    border-left: 4px solid #667eea;
    padding: 15px 20px;
    margin-top: 20px;
    border-radius: 0 8px 8px 0;
}

.highlight p {
    margin: 0;
    font-weight: 500;
    color: #333;
}

.warning-box {
    background: #fff3cd;
    border: 1px solid #ffeaa7;
    border-radius: 10px;
    padding: 20px;
    margin-top: 30px;
    text-align: center;
}

.warning-box p {
    margin: 0;
    color: #856404;
    font-weight: 500;
}

.contact-section {
    margin-top: 60px;
    text-align: center;
}

.contact-card {
    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
    color: white;
    border-radius: 20px;
    padding: 40px;
    max-width: 500px;
    margin: 0 auto;
}

.contact-card h3 {
    font-size: 1.5rem;
    margin-bottom: 15px;
}

.contact-card p {
    margin-bottom: 25px;
    opacity: 0.9;
}

.btn-primary {
    display: inline-block;
    background: white;
    color: #667eea;
    padding: 12px 30px;
    border-radius: 25px;
    text-decoration: none;
    font-weight: 600;
    transition: all 0.3s ease;
    border: 2px solid white;
}

.btn-primary:hover {
    background: transparent;
    color: white;
    transform: translateY(-2px);
}

/* Responsive */
@media (max-width: 768px) {
    .hero-section h1 {
        font-size: 2.2rem;
    }

    .hero-section {
        padding: 60px 20px;
    }

    .formation-card, .financing-card {
        padding: 20px;
    }

    .formations-section h2, .financing-section h2 {
        font-size: 1.6rem;
    }

    .contact-card {
        padding: 30px 20px;
    }
}
